const buyModal=document.querySelector(".product-in-basket"),buyButtonsOpen=document.querySelectorAll(".perforators-item-button-buy"),buyButtonClose=buyModal.querySelector(".button-close");buyButtonsOpen.forEach(function(e){e.addEventListener("click",function(e){e.preventDefault(),buyModal.classList.add("modal-show-grid")})}),buyButtonClose.addEventListener("click",function(e){e.preventDefault(),buyModal.classList.remove("modal-show-grid")}),window.addEventListener("keydown",function(e){"Esc"!==e.key&&"Escape"!==e.key||buyModal.classList.contains("modal-show-grid")&&(e.preventDefault(),buyModal.classList.remove("modal-show-grid"))});
